const getStyleName=e=>e.replace(/([A-Z])/g,"-$1").toLowerCase(),mixStyles=e=>{const n=Object.entries(window.brushLog||{}).reduce((e,n)=>`${e}${getStyleName(n[0])}:${n[1]};`,""),o=`\n    color: #000;\n    margin: 2px 0 4px 0;\n    border-radius: 14px;\n    padding: 6px 10px;\n    font-weight: bold;\n    background: #CDE5F9;\n    border: 1px dashed #000;\n    ${n}\n    ${e?`background: ${e};`:""}\n    `;return o},log=(e,n,o)=>{console.log(`%c${e}`,mixStyles(o),n)};export default log;export const brush=(e,n,o)=>[`%c${e}`,mixStyles(o),n];